struct PaletteShowcase: View {
    private static let categoryOrder: [PaletteColorCategory] = [.greyscale, .gold, .red, .green, .orange, .blue, .fuchsia, .other]
    
    var body: some View {
        List {
            Section(header: Text("UI Palette")) {
                Text("Palette colors grouped by category (matches Figma)")
                    .font(.caption)
                    .foregroundColor(.secondary)
            }
            
            ForEach(Self.categoryOrder, id: \.self) { category in
                let colorsInCategory = Palette.allCases.filter { $0.category == category }
                if !colorsInCategory.isEmpty {
                    Section(header: HStack {
                        Text(categoryDisplayName(category))
                            .font(.subheadline)
                            .fontWeight(.semibold)
                        Spacer()
                        Text("\(colorsInCategory.count)")
                            .font(.system(.caption, design: .monospaced))
                            .foregroundColor(.secondary)
                    }) {
                        ForEach(Array(colorsInCategory), id: \.self) { color in
                            PaletteColorRow(color: color)
                        }
                    }
                }
            }
        }
        .navigationTitle("Palette")
        .toolbar {
            Text("\(Palette.allCases.count)")
                .font(.system(.body, design: .monospaced))
                .foregroundColor(.secondary)
        }
    }
    
    private func categoryDisplayName(_ category: PaletteColorCategory) -> String {
        switch category {
        case .greyscale: return "Greyscale"
        case .gold: return "Gold"
        case .red: return "Red"
        case .green: return "Green"
        case .orange: return "Orange"
        case .blue: return "Blue"
        case .fuchsia: return "Fuchsia"
        case .other: return "Other"
        }
    }
}

